// Invio documenti contabili via e-mail
// Per le impostazioni SMTP ora usiamo un file email_custom.php


//debug($data);

if (!$data['post']['documenti_contabilita_mail_documento_id']) {
    throw new ApiException("Documento non esistente, se il problema persiste contattare l'assistenza tecnica.");
    exit();
}

$documento = $this->apilib->view('documenti_contabilita', $data['post']['documenti_contabilita_mail_documento_id']);
$settings = $this->apilib->searchFirst('documenti_contabilita_settings');

// invio email
$this->load->library('email');
$config['charset'] = 'utf-8';
$config['wordwrap'] = TRUE;
$config['mailtype'] = 'html';

$this->email->initialize($config);
$this->email->from($data['post']['documenti_contabilita_mail_mittente'], $data['post']['documenti_contabilita_mail_mittente_nome']);

$documento_numero = $documento['documenti_contabilita_numero'];

// SE fattura elettronica allego anche la preview xml
if ($documento['documenti_contabilita_formato_elettronico'] == DB_BOOL_TRUE) {

        if ($data['post']['documenti_contabilita_mail_template']) {
            $allegato = base64_decode($documento['documenti_contabilita_file_preview_xml']);
            $this->email->attach($allegato, 'attachment', 'Fattura_elettronica_'.$documento_numero.'.pdf', 'application/pdf');
        }

        // Allegato il pdf standard
        $allegato = base64_decode($documento['documenti_contabilita_file_preview']);
        $this->email->attach($allegato, 'attachment', 'Documento_'.$documento_numero.'.pdf', 'application/pdf');

        // Allego l'xml
        /*$allegato = base64_decode($documento['documenti_contabilita_file']);
        $nomefile = "IT" . $settings['documenti_contabilita_settings_company_vat_number'] . "_00001.xml";
        $this->email->attach($allegato, 'attachment', $nomefile, 'application/xml');*/

} else {

    // Allegato il pdf standard
    $allegato = base64_decode($documento['documenti_contabilita_file']);
    $this->email->attach($allegato, 'attachment', 'Documento_'.$documento_numero.'.pdf', 'application/pdf');

}


if ($documento['documenti_contabilita_serie']) {
    $documento_numero = $documento_numero . '/' . $documento['documenti_contabilita_serie'];
}

// Replace
$data['post']['documenti_contabilita_mail_oggetto'] = str_replace('{numero_fattura}', $documento_numero, $data['post']['documenti_contabilita_mail_oggetto']);
$data['post']['documenti_contabilita_mail_oggetto'] = str_replace('{data_emissione}', date('d-m-Y', strtotime($documento['documenti_contabilita_data_emissione'])), $data['post']['documenti_contabilita_mail_oggetto']);

$data['post']['documenti_contabilita_mail_testo'] = str_replace('{numero_fattura}', $documento_numero, $data['post']['documenti_contabilita_mail_testo']);
$data['post']['documenti_contabilita_mail_testo'] = str_replace('{data_emissione}', date('d-m-Y', strtotime($documento['documenti_contabilita_data_emissione'])), $data['post']['documenti_contabilita_mail_testo']);


$this->email->to($data['post']['documenti_contabilita_mail_destinatario']);
$this->email->subject($data['post']['documenti_contabilita_mail_oggetto']);

$this->email->message($data['post']['documenti_contabilita_mail_testo'] ?: '&nbsp;');

$data['post']['documenti_contabilita_mail_allegati'] = json_encode(array(base64_encode($allegato)));

// Send and return the result
$return = $this->email->send();
if (empty($return)) {   
    throw new ApiException("Invio mail fallito: ".$this->email->print_debugger());
//    exit();
}


